export const defaultFontSize=55;export const fontSizeOffset=15;const mainHeader=document.querySelector("header[data-page='landing']");mainHeader&&document.documentElement.style.setProperty("--mainHeaderHeight",mainHeader.clientHeight+"px");export function showPageContent(){document.body.setAttribute("data-content","loaded")}export function animateSubmitBtn(e){e.querySelector("button[type='submit']").classList.toggle("js-checking")}export function adjustExpandalbeInput(e){const t=e.querySelector("textarea"),n=e.querySelector("span");t.addEventListener("input",(e=>{n.textContent=t.value,"insertLineBreak"===e.inputType&&(n.innerHTML+="<br>")}))}export function getUserPreferences(){return JSON.parse(localStorage.getItem("postItUserPreferences"))}export function setUserPreferences(e,t){let n=getUserPreferences();n[e]=t,localStorage.setItem("postItUserPreferences",JSON.stringify(n))}export function togglePageTheme(){document.documentElement.setAttribute("data-dark-theme",getUserPreferences().darkTheme)}export function changeFontsize(){let e=(getUserPreferences().fontSize/55).toPrecision(5);document.documentElement.style.setProperty("--fs-factor",e)}