import{createErrorMessage}from"./formValidation.js";const baseURL="https://tarmeezacademy.com/api/v1/",requestsURLs={login:()=>"login",signup:()=>"register",logout:()=>"logout",getUser:([s]=args)=>`users/${s}`,getUserPosts:([s]=args)=>`users/${s}/posts`,posts:([s]=args)=>"posts"+(s?"/"+s:""),postComments:([s]=args)=>`posts/${s}/comments`};export function getRequestURL(s,...e){return baseURL+requestsURLs[s](e)}export async function makeRequest(s){const{url:e,method:t="GET"}=s,o=await fetch(e,{method:t,headers:{Accept:"application/json",...s.headers},body:s.body}),r=await o.json();if(o.ok)return r;createErrorMessage(r.message)}