showPageContent();import{validateFormData}from"../auth/formValidation.js";import{showPageContent,animateSubmitBtn}from"../abstracts/styles.js";import{makeRequest,getRequestURL}from"../auth/requests.js";import{logUserIn}from"../auth/authorization.js";const loginForm=document.getElementById("login-form");async function handleLogin(t){if(t.preventDefault(),!validateFormData(loginForm))return;animateSubmitBtn(loginForm);const o=await makeRequest({method:"POST",url:getRequestURL("login"),headers:{},body:new FormData(loginForm)});o&&logUserIn(o),animateSubmitBtn(loginForm)}loginForm.addEventListener("submit",(t=>handleLogin(t)));